import { useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router";
import Loader from "../../component/Loader";
import {
  clearCartAction,
  deleteFromCartAction,
} from "../../lib/slices/cartSlice";
import type { AppDispatch, RootState } from "../../lib/store/store";
// import img1 from "react-store/src/assets/image1.jpg";
const Cart = () => {
  const { productsCart, getLoading, removeLoading, clearLoading } = useSelector(
    (state: RootState) => state.cartReducer
  );

  const dispatch: AppDispatch = useDispatch();

  const subtotal = useMemo(() => {
    return (
      productsCart?.reduce(
        (acc, item) => acc + item.productId.price * item.quantity,
        0
      ) || 0
    );
  }, [productsCart]);

  const shipping = 150;
  const total = subtotal + shipping;

  return (
    <div className=" relative flex justify-center items-center  px-4 py-8 ">
      {removeLoading || getLoading || clearLoading ? (
        <div className="absolute inset-0 bg-white bg-opacity-50 z-10 flex items-center justify-center">
          <Loader />
        </div>
      ) : productsCart?.length === 0 ? (
        <div className="flex flex-col items-center justify-center text-center space-y-6 py-20">
          <p className="text-2xl font-semibold text-gray-700">
            Your Cart is Empty
          </p>
          <img
            src="data:image/png;base64,"
            alt="Empty Cart"
            className="w-40 h-40 object-contain"
          />

          <Link to="/">
            <button className="bg-teal-600 text-white px-6 py-2 rounded hover:bg-teal-700 transition">
              Go to Home
            </button>
          </Link>
        </div>
      ) : (
        <div className="w-full  max-w-5xl mx-auto px-4 bg-white rounded-lg shadow p-8">
          <div className="flex mb-3 justify-between items-baseline    ">
            <div>
              <h3 className="text-3xl font-bold  text-left">Shopping Cart</h3>
            </div>

            <button
              onClick={() => {
                dispatch(clearCartAction());
              }}
              className="text-red-600 border cursor-pointer   border-red-600 px-4 py-1 rounded hover:bg-red-600 hover:text-white transition">
              Clear Cart
            </button>
          </div>
          <div className="flex flex-col md:flex-row gap-6">
            <div className="flex-1  ">
              {productsCart?.map((item) => {
                const { productId, _id } = item;
                return (
                  <div
                    key={_id}
                    className="flex flex-col text-center md:text-start md:flex-row gap-6 mb-5 bg-gray-50 shadow-md p-6 rounded-lg  ">
                    <img
                      src={productId.imageCover}
                      className="w-28 h-28 object-cover rounded-lg m-auto"
                    />
                    <div className="flex-1 flex flex-col justify-between">
                      <h3 className="text-lg font-semibold">
                        {productId.title.split(" ").slice(0, 2).join(" ")}
                      </h3>
                      <p className="text-sm text-gray-600 text-xl">
                        {productId.category.name}
                      </p>
                      <p className="text-gray-600 text-xl">
                        EGP {productId.price}
                      </p>
                    </div>
                    <div className="flex flex-col items-center justify-between gap-4">
                      <button
                        onClick={() => {
                          dispatch(deleteFromCartAction(productId._id));
                        }}
                        className="text-red-600 border cursor-pointer border-red-600 px-4 py-1 rounded hover:bg-red-600 hover:text-white transition">
                        Remove
                      </button>
                      {/* <div className="flex items-center gap-2">
                        <button
                          onClick={() => decrement(quantity)}
                          className="w-8 h-8 bg-gray-200 rounded text-lg font-bold">
                          -
                        </button>
                        <span className="text-md font-medium">{quantity}</span>
                        <button
                          onClick={() => increment(quantity)}
                          className="w-8 h-8 bg-gray-200 rounded text-lg font-bold">
                          +
                        </button>
                      </div> */}
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="w-full md:w-80 border border-gray-300 rounded-lg shadow-md h-fit p-4">
              <div className="mb-4 ">
                <div className="flex justify-between text-gray-700">
                  <span>Subtotal</span>
                  <span>EGP {subtotal.toFixed(2)}</span>
                </div>
                <div className="flex justify-between text-gray-700">
                  <span>Shipping</span>
                  <span>EGP {shipping.toFixed(2)}</span>
                </div>
                <hr className="my-2" />
                <div className="flex justify-between font-bold text-lg">
                  <span>Total</span>
                  <span>EGP {total.toFixed(2)}</span>
                </div>
              </div>
              <Link to="/checkout">
                {" "}
                <button className="w-full cursor-pointer bg-teal-700 hover:bg-teal-800 text-white py-2 rounded transition">
                  Checkout
                </button>
              </Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
